mapper.insert:
insert into member_grade_rule (id, code, name, 
  rule_content, create_by, create_time, 
  modify_by, modify_time, tflag,parent_code,rule_type
  )
values (#{id,jdbcType=DECIMAL}, #{code,jdbcType=VARCHAR}, #{name,jdbcType=VARCHAR}, 
  #{ruleContent,jdbcType=VARCHAR}, #{createBy,jdbcType=VARCHAR}, #{createTime,jdbcType=TIMESTAMP}, 
  #{modifyBy,jdbcType=VARCHAR}, #{modifyTime,jdbcType=TIMESTAMP}, #{tflag,jdbcType=VARCHAR},#{parentCode,jdbcType=VARCHAR},
  #{ruleType,jdbcType=VARCHAR}
  )

mapper.select:
select
from member_grade_rule where code in
#{code}

mapper.delete:
delete from member_grade_rule
where code in

  #{code}
